import logo from './logo.svg';
import './App.css';
import React from 'react';
import Slider from '@material-ui/core/Slider';



class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonText: 'OFF',
            sliderCurrValue: 10,
        }
        this.handleSlider = this.handleSlider.bind(this);
        this.handleButton = this.handleButton.bind(this);
    }
    handleButton(){
        if(this.state.buttonText === 'OFF'){
            this.setState({
                buttonText: 'ON'
            });
        } else{
            this.setState({
                buttonText: 'OFF'
            });
        }
    }
    handleSlider(e, value){
        console.log(value)
        this.setState({
            sliderCurrValue: value
        });
    }
    render(){
        return(
            <div>
                <button onClick={this.handleButton}>{this.state.buttonText}</button>
                <div> {/*connection status*/}

                </div>
                <Slider value={this.state.sliderCurrValue} defaultValue={15} onChange={this.handleSlider} aria-labelledby="continuous-slider" />
            </div>
        );
    }
