import logo from './logo.svg';
import './App.css';
import React from 'react';
import Slider from '@material-ui/core/Slider';
import RefreshIcon from '@material-ui/icons/Refresh';
import { withStyles, makeStyles } from '@material-ui/core/styles';


class App extends React.Component {
    constructor(props) {
        super(props);
        this.refreshAnimationFree = true;
        this.state = {
            buttonText: 'OFF',
            sliderCurrValue: 10,
            connectionStatus: 'Connecting',
        }
        this.handleSlider = this.handleSlider.bind(this);
        this.handleButton = this.handleButton.bind(this);
        this.sendSliderReq = this.sendSliderReq.bind(this);
        this.handleRefresh = this.handleRefresh.bind(this);
    }
    handleButton(){
        if(this.state.buttonText === 'OFF'){
            this.setState({
                buttonText: 'ON'
                // buttonColor: ''
            });
        } else{
            this.setState({
                buttonText: 'OFF'
            });
        }
    }
    handleSlider(e, value){
        if(this.state.sliderCurrValue != value){
            console.log(value)
            this.setState({
            sliderCurrValue: value
        });
        }
    }
    sendSliderReq(e, value){
        console.log("sending request");
    }

    tmp(){
        console.log("animation end");
        document.querySelector('.refresh').classList.remove('refreshRotate');
        document.querySelector('.refresh').removeEventListener('animationend', this.tmp);
    }

    handleRefresh(e){
        console.log("refresh");
        document.querySelector('.refresh').classList.add('refreshRotate');
        document.querySelector('.refresh').addEventListener('animationend', this.tmp);
    }
    render(){
        return(
            <div className={"container"}>
                <button
                    className={"refresh"}
                    onClick={this.handleRefresh}>
                    <RefreshIcon
                        fontSize={"large"}/>
                </button>
                <button
                    onClick={this.handleButton}
                    className="switchButton">
                    {this.state.buttonText}
                </button>
                <div className={"connectionStatus"}> {/*connection status*/}
                    <span>{this.state.connectionStatus}</span>
                </div>
                <IOSSlider
                    value={this.state.sliderCurrValue}
                    min={0}
                    max={30}
                    step={1}
                    onChange={this.handleSlider}
                    onChangeCommitted={this.sendSliderReq}
                    aria-labelledby="continuous-slider" />

            </div>
        );
    }
  // return (
  //     <div>
  //       <button></button>
  //     </div>
  //   // <div className="App">
  //   //   <header className="App-header">
  //   //     <img src={logo} className="App-logo" alt="logo" />
  //   //     <p>
  //   //       Edit <code>src/App.js</code> and save to reload.
  //   //     </p>
  //   //     <a
  //   //       className="App-link"
  //   //       href="https://reactjs.org"
  //   //       target="_blank"
  //   //       rel="noopener noreferrer"
  //   //     >
  //   //       Learn React
  //   //     </a>
  //   //   </header>
  //   // </div>
  // );
}

export default App;
const iOSBoxShadow = '0 3px 1px rgba(0,0,0,0.1),0 4px 8px rgba(0,0,0,0.13),0 0 0 1px rgba(0,0,0,0.02)';
const IOSSlider = withStyles({
  root: {
    color: '#3880ff',
    height: 2,
    padding: '15px 0',
  },
  thumb: {
    height: 28,
    width: 28,
    backgroundColor: '#fff',
    boxShadow: iOSBoxShadow,
    marginTop: -14,
    marginLeft: -14,
    '&:focus, &:hover, &$active': {
      boxShadow: '0 3px 1px rgba(0,0,0,0.1),0 4px 8px rgba(0,0,0,0.3),0 0 0 1px rgba(0,0,0,0.02)',
      // Reset on touch devices, it doesn't add specificity
      '@media (hover: none)': {
        boxShadow: iOSBoxShadow,
      },
    },
  },
  active: {},
  valueLabel: {
    left: 'calc(-50% + 12px)',
    top: -22,
    '& *': {
      background: 'transparent',
      color: '#000',
    },
  },
  track: {
    height: 2,
  },
  rail: {
    height: 2,
    opacity: 0.5,
    backgroundColor: '#bfbfbf',
  },
  mark: {
    backgroundColor: '#bfbfbf',
    height: 8,
    width: 1,
    marginTop: -3,
  },
  markActive: {
    opacity: 1,
    backgroundColor: 'currentColor',
  },
})(Slider);
